import React from "react";
import axios from "axios";
import "./CSS/Signup.css";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  extendedIcon: {
    marginRight: theme.spacing(1),
  },
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 200,
    },
  },
}));

export default function SignUp({
  firstname,
  lastname,
  email,
  phone,
  setFirstname,
  setLastname,
  setEmail,
  setPhone,
  id,
  name,
  reset,
  setReset,
}) {
  const classes = useStyles();

  async function handleParticipantRegisteration(e) {
    e.preventDefault();
    debugger
    try {
      const baseUrl = `http://localhost:4100`;
      const endpoint = `/raffles/${id}/participants`;
      const data = {
        firstname: firstname,
        lastname: lastname,
        email: email,
        phone: phone,
      };
      const addParticipant = await axios.post(`${baseUrl}${endpoint}`, data);
      // setParticipants(participants.push(addParticipant));
      // console.log(participants);
    } catch (error) {
      console.log("error", error);
    }
  }

  function resetForm(e) {
    e.preventDefault();
    setReset("");
  }

  return (
    <div className="sign-up">
      <h2>{name}</h2>
      <form onSubmit={handleParticipantRegisteration}>
        <div className="names">
          <div className="firstname">
            <label>First Name*</label>
            {/* <TextField
              // error
              id="standard-error-helper-text"
              label="Firstname*"
              defaultValue={firstname}
              // helperText="Incorrect entry."
              onChange={(e) => setFirstname(e.target.value)}
            /> */}

            <input
              type="text"
              onChange={(e) => setFirstname(e.target.value)}
              value={firstname}
            />
          </div>
          <div className="lastname">
            <label>Last Name*</label>
            {/* <TextField
              // error
              id="standard-error-helper-text"
              onChange={(e) => setLastname(e.target.value)}
              defaultValue={lastname}
              label="Lastname*"

              // helperText="Incorrect entry."
            /> */}
            <input
              type="text"
              onChange={(e) => setLastname(e.target.value)}
              value={lastname}
            />
          </div>
        </div>
        <div className="email">
          <label>Email*</label>
          {/* <TextField
            // error
            id="standard-error-helper-text"
            label="Email*"
            onChange={(e) => setEmail(e.target.value)}
            defaultValue={email}
            // helperText="Incorrect entry."
          /> */}
          <input
            type="text"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
        </div>
        <div className="phone">
          <label>Phone</label>
          {/* <TextField
            // error
            id="standard-error-helper-text"
            label="Phone"
            onChange={(e) => setPhone(e.target.value)}
            defaultValue={phone}
            // helperText="Incorrect entry."
          /> */}
          <input
            type="text"
            onChange={(e) => setPhone(e.target.value)}
            value={phone}
          />
        </div>
        <div className="signup-btn">
          <Button
            variant="outlined"
            size="large"
            color="primary"
            className={classes.margin}
          >
            Submit
          </Button>
          <Button
            variant="outlined"
            size="large"
            color="primary"
            className={classes.margin}
          >
            Reset
          </Button>
        </div>
      </form>
    </div>
  );
}
