import React, { useState } from "react";
import axios from "axios";

/**
 * create a text(link) to randomize a token
 * p 
 * 
 */

export default function NewRaffle({
  name,
  setName,
  secretToken,
  setSecretToken,
  setReset,
  reset,
  msg,
  setMsg,
  submitted,
  setSubmitted,
}) {
  function handleName(e) {
    setName(e.target.value);
  }

  function handleToken(e) {
    setSecretToken(e.target.value);
  }

  async function handleSubmit(e) {
    e.preventDefault();

    if (name && secretToken) {
      try {
        const baseUrl = `http://localhost:4100`;
        const endpoint = `/raffles`;
        const data = {
          name: name,
          secret_token: secretToken,
        };
        const addRaffle = await axios.post(`${baseUrl}${endpoint}`, data);
        setSubmitted(true)
        setMsg('Successfully entered a new raffle')
      } catch (error) {
        console.log("error", error);
      }
    } else {
      setMsg('Please enter a name and secret token!')
        setReset('')
    }
  }


  return (
    <div className="new-raffle">
      <h2>New Raffle:</h2>
      <form onSubmit={handleSubmit}>
        <div className="inputs">
          <div>{msg}</div>
          <label>Raffle Name:*</label>
          <br />
          <input type="text" onChange={handleName} value={name}/>
          <br />
          <label>Raffle Secret Token:*</label>
          <br />
          <input type="text" onChange={handleToken} value={secretToken} disabled={submitted}/>
        </div>
        <div>You must remember the Raffle Token because it will be asked when picking a winner</div>
        <div className="btn-submit">
          <button>Create New Raffle</button>
        </div>
      </form>
    </div>
  );
}
